import solution from './0005';

/**
 * @author sabertazimi
 * @license MIT
 * @level medium
 * @description longest-palindromic-substring
 * Given a string `s`,
 * return the longest palindromic substring in `s`.
 */
describe('LeetCode [0005]', () => {
  test('should AC', () => {
    expect(solution('')).toStrictEqual('');
    expect(solution('a')).toStrictEqual('a');
    expect(solution('ac')).toStrictEqual('a');
    expect(solution('bb')).toStrictEqual('bb');
    expect(solution('ccd')).toStrictEqual('cc');
    expect(solution('cbbd')).toStrictEqual('bb');
    expect(solution('babad')).toStrictEqual('aba');
    expect(solution('abcba')).toStrictEqual('abcba');
    expect(solution('eabcb')).toStrictEqual('bcb');
    expect(
      solution(
        ''
      )
    ).toStrictEqual(
      ''
    );
  });
});
