import solution from './0005';

/**
 * @author sabertazimi
 * @license MIT
 * @level medium
 * @description longest-palindromic-substring
 * Given a string `s`,
 * return the longest palindromic substring in `s`.
 */
describe('LeetCode [0005]', () => {
  test('should AC', () => {
    expect(solution('')).toBe('');
    expect(solution('a')).toBe('a');
    expect(solution('ac')).toBe('a');
    expect(solution('bb')).toBe('bb');
    expect(solution('ccd')).toBe('cc');
    expect(solution('cbbd')).toBe('bb');
    expect(solution('babad')).toBe('aba');
    expect(solution('abcba')).toBe('abcba');
    expect(solution('eabcb')).toBe('bcb');
    expect(
      solution(
        ''
      )
    ).toBe(
      ''
    );
  });
});
